            "14o66qrk": {
                "type": "html-box",
                "specials": {
                    "html": "&lt;!-- ====== AUDIO &amp; TOGGLE (HTML Box) ====== --&gt;\r\n&lt;!-- Nhạc nền --&gt;\r\n&lt;audio\r\n  id=&quot;background-music&quot;\r\n  src=&quot;https://statics.pancake.vn/web-media/5e/ee/bf/4a/afa10d3bdf98ca17ec3191ebbfd3c829d135d06939ee1f1b712d731d-w:0-h:0-l:2938934-t:audio/mpeg.mp3&quot;\r\n  preload=&quot;none&quot;\r\n  loop\r\n&gt;&lt;/audio&gt;\r\n\r\n&lt;!-- Nút toggle nhạc (cố định góc trái dưới) --&gt;\r\n&lt;div id=&quot;music-toggle&quot; title=&quot;Bật / Tắt nhạc&quot; aria-label=&quot;Bật / Tắt nhạc&quot;&gt;\r\n  &lt;!-- PNG khi NHẠC TẮT --&gt;\r\n  &lt;img\r\n    id=&quot;play-icon&quot;\r\n    src=&quot;https://content.pancake.vn/1/02/d4/a7/88/fef5132f979892c1778a688f2039942fc24b396b332750179775f87e-w:200-h:200-l:8183-t:image/png.png&quot;\r\n    alt=&quot;Play&quot;\r\n    width=&quot;44&quot;\r\n    height=&quot;44&quot;\r\n    decoding=&quot;async&quot;\r\n    loading=&quot;lazy&quot;\r\n  /&gt;\r\n  &lt;!-- GIF khi NHẠC BẬT --&gt;\r\n  &lt;img\r\n    id=&quot;pause-icon&quot;\r\n    src=&quot;https://content.pancake.vn/1/31/08/c9/52/c9f574ca2fa8481e1c8c657100583ddfbf47e33427d480a7dc32e788-w:200-h:200-l:242141-t:image/gif.gif&quot;\r\n    alt=&quot;Pause&quot;\r\n    width=&quot;44&quot;\r\n    height=&quot;44&quot;\r\n    decoding=&quot;async&quot;\r\n    loading=&quot;lazy&quot;\r\n    style=&quot;display:none&quot;\r\n  /&gt;\r\n&lt;/div&gt;\r\n\r\n&lt;style&gt;\r\n  /* ====== STYLE: vị trí &amp; hiệu ứng nút ====== */\r\n  #music-toggle {\r\n    position: fixed;\r\n    left: 14px;   /* ghim bên trái */\r\n    bottom: 14px;\r\n    z-index: 99999;\r\n    width: 56px;\r\n    height: 56px;\r\n    border-radius: 50%;\r\n    background: rgba(255, 255, 255, 0.3); /* nền trắng */\r\n    backdrop-filter: blur(4px);\r\n    display: grid;\r\n    place-items: center;\r\n    cursor: pointer;\r\n    user-select: none;\r\n    transition: transform 0.2s ease, box-shadow 0.2s ease, background 0.2s ease;\r\n    box-shadow: 0 6px 20px rgba(0, 0, 0, 0.15);\r\n  }\r\n  #music-toggle:hover {\r\n    transform: translateY(-1px) scale(1.03);\r\n    box-shadow: 0 10px 24px rgba(0, 0, 0, 0.18);\r\n  }\r\n  #music-toggle img {\r\n    display: block;\r\n    width: 44px;\r\n    height: 44px;\r\n    object-fit: contain;\r\n    pointer-events: none;\r\n  }\r\n\r\n\r\n  #music-toggle.vibrating {\r\n    animation: wc-pulse 1.6s ease-out infinite;\r\n    background: rgba(255, 255, 255, 0.3); /* nền trắng khi đang rung */\r\n  }\r\n\r\n  /* Ép hiển thị đúng icon trong mọi theme */\r\n  #music-toggle #play-icon,\r\n  #music-toggle #pause-icon {\r\n    backface-visibility: visible !important;\r\n    -webkit-backface-visibility: visible !important;\r\n  }\r\n\r\n  /* Tối ưu chạm cho mobile */\r\n  @media (hover: none) {\r\n    #music-toggle {bottom: 12px; left: 12px; }\r\n  }\r\n&lt;/style&gt;\r\n\r\n&lt;script&gt;\r\n  document.addEventListener(&quot;DOMContentLoaded&quot;, function () {\r\n    const audio = document.getElementById(&quot;background-music&quot;);\r\n    const toggleButton = document.getElementById(&quot;music-toggle&quot;);\r\n    const playIcon = document.getElementById(&quot;play-icon&quot;);   // PNG (nhạc tắt)\r\n    const pauseIcon = document.getElementById(&quot;pause-icon&quot;); // GIF (nhạc bật)\r\n\r\n    audio.volume = 0.6;\r\n    let isPlaying = false;\r\n    let hasStarted = false;\r\n\r\n    function setUI(playing) {\r\n      if (playing) {\r\n        playIcon.style.display = &quot;none&quot;;\r\n        pauseIcon.style.display = &quot;block&quot;;\r\n        toggleButton.classList.add(&quot;vibrating&quot;);\r\n      } else {\r\n        playIcon.style.display = &quot;block&quot;;\r\n        pauseIcon.style.display = &quot;none&quot;;\r\n        toggleButton.classList.remove(&quot;vibrating&quot;);\r\n      }\r\n    }\r\n\r\n    const startOnce = () =&gt; {\r\n      if (!hasStarted) {\r\n        audio.play().then(() =&gt; {\r\n          isPlaying = true;\r\n          hasStarted = true;\r\n          setUI(true);\r\n        }).catch(() =&gt; {setUI(false); });\r\n      }\r\n    };\r\n\r\n    document.body.addEventListener(&quot;click&quot;, startOnce, {once: true });\r\n    document.body.addEventListener(&quot;touchstart&quot;, startOnce, {once: true, passive: true });\r\n\r\n    toggleButton.addEventListener(&quot;click&quot;, function (event) {\r\n      event.stopPropagation();\r\n      if (isPlaying) {\r\n        audio.pause();\r\n        isPlaying = false;\r\n        setUI(false);\r\n      } else {\r\n        audio.play().then(() =&gt; {\r\n          isPlaying = true;\r\n          hasStarted = true;\r\n          setUI(true);\r\n        }).catch(() =&gt; { });\r\n      }\r\n    });\r\n\r\n    audio.addEventListener(&quot;play&quot;, () =&gt; {isPlaying = true; setUI(true); });\r\n    audio.addEventListener(&quot;pause&quot;, () =&gt; {isPlaying = false; setUI(false); });\r\n  });\r\n&lt;/script&gt;\r\n"
                },
                "id": "14o66qrk",
                "$parent": "5fieb1sl"
            },
